mapper.select:
/*JoinDao.findUserSn*/
SELECT CONCAT(GROUP_CD_NM,SN_NO) AS USER_SN
FROM (SELECT TO_CHAR(NEXTVAL('TB_CMMN_AUTHOR_USER_SEQ'), '00000000FM') AS SN_NO) AS A , TB_CMMN_CODE_GROUP
WHERE GROUP_CD_NM = #{groupNm}

mapper.insert:
INSERT INTO TB_USER_INFO
(USER_SN
, USER_ID
, USER_PWD
, USER_NM
, USER_BIRTH
, USER_MPNO
, REAL_ADDR
, REAL_ADDR_DTL
, ADDR_UPDT_DT
, WITHDRAWAL_DT
)
VALUES
(#{userSn}
, #{userId}
, #{userPwd}
, #{userNm}
, #{userBirth}
, #{userMpno}
, #{realAddr}
, #{realAddrDtl}
, now()
, now()
)
